mapper.update:
UPDATE article
SET hits = hits + 1
WHERE article_id = #{articleId}

mapper.delete:
DELETE FROM reply
WHERE reply_id = #{replyId}
AND replier_id = #{replierId}

mapper.update:
UPDATE reply SET content = #{reply.content}
WHERE reply_id = #{reply.replyId}
AND replier_id = #{reply.replierId}
